import { Col, Card, ButtonGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'

import './Book-card.css'


saveFav = (jobId) => {
    const favourites = [...this.props.loggedInUser.favourites]
    favourites.push(jobId)
    const updatedFavourites = [...favourites]
    const updateUser = { ...this.props.loggedInUser, favourites: updatedFavourites }
    this.userService.editUserInfo(this.props.loggedInUser._id, updateUser)
        .then((response) => {
            this.props.setTheUser(response.data)
        })
        .catch(err => console.log(err))
    //this.setState({ favourites: updatedFavourites })
}


const BookCard = props => {

    return (
        <Col lg={4}>
            <Card className="book-card">
                <Card.Img variant="top" src={props.image} />
                <Card.Body>
                    <Card.Title>{props.title}</Card.Title>
                    <ButtonGroup aria-label="Basic example" style={{ width: '100%' }}>

                        <Link className="btn btn-dark" to={`/libros/editar/${props._id}`}>Editar</Link>
                        <Link className="btn btn-dark" to={`/libros/${props._id}`}>Ver detalles</Link>
                    </ButtonGroup>
                </Card.Body>
            </Card>
        </Col>
    )
}

export default BookCard
