    constructor() {
        super()
        this.state = {
            book: []
        }
        this.booksService = new BooksService()
        console.log(this.state)
    }

    handleInputChange = e => this.setState({ [e.target.name]: e.target.value })

    handleSubmit = e => {

        e.preventDefault()

        this.booksService
            .saveBook(this.state)
            .then(res => {
                console.log(this.props)
                this.props.history.push('/libros')
            })
            .catch(err => console.log(err))
    }


    render() {

        return (
