import React, { Component } from 'react';
import EventService from "../../../service/event.service"

import { GoogleMap, withScriptjs, withGoogleMap } from 'react-google-maps';


class Maps extends Component {

    constructor(props) {
        super(props)
        this.state = {
            events: {
                name: "",
                longitude: "",
                latitude:"",
            }
        }
        this.eventService = new EventService()

    }

    componentDidMount = () => {
        this.refreshEvents()
    }

    refreshEvents = () => {

        this.eventService
            .getEvent()
            .then(res => {  

                this.setState({ events: { ...this.state.events, [this.state.events]: res}})

    })
            .catch(err => console.log(err))
    }

    render() {

        const latitude = this.state.events

        console.log(latitude)

        return (

            <GoogleMap

                defaultZoom={15}
                defaultCenter={{ lat: parseInt(this.state.events.latitude), lng: parseInt(this.state.events.longitude) }}

            />
        )
    }
}

export default withScriptjs(withGoogleMap(Maps))
